import React from 'react';
import { View, Text, StyleSheet, Pressable } from 'react-native';
import { useFormContext } from '../context/FormContext';

const BeastModeQuestion = () => {
  const { formData, updateFormData, nextQuestion } = useFormContext();
  const selectedYeti = formData.beastMode;

  const handleSelect = (yeti) => {
    updateFormData('beastMode', yeti);
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Pick your beast mode</Text>

      <View style={styles.options}>
        <Pressable 
          style={[
            styles.option,
            selectedYeti === 'White Yeti' && styles.selectedOption,
            { backgroundColor: selectedYeti === 'White Yeti' ? '#3182CE' : '#fff' }
          ]}
          onPress={() => handleSelect('White Yeti')}
        >
          <Text 
            style={[
              styles.optionText,
              selectedYeti === 'White Yeti' && styles.selectedText
            ]}
          >
            White Yeti
          </Text>
        </Pressable>

        <Pressable 
          style={[
            styles.option,
            selectedYeti === 'Black Yeti' && styles.selectedOption,
            { backgroundColor: selectedYeti === 'Black Yeti' ? '#3182CE' : '#fff' }
          ]}
          onPress={() => handleSelect('Black Yeti')}
        >
          <Text 
            style={[
              styles.optionText,
              selectedYeti === 'Black Yeti' && styles.selectedText
            ]}
          >
            Black Yeti
          </Text>
        </Pressable>
      </View>

      <View style={styles.navigation}>
        <View style={{ width: 88 }} /> {/* Spacer to maintain layout */}
        <Pressable 
          style={[styles.nextButton, !selectedYeti && styles.nextButtonDisabled]}
          disabled={!selectedYeti}
          onPress={nextQuestion}
        >
          <Text style={styles.nextButtonText}>Next â†’</Text>
        </Pressable>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    width: '100%',
    maxWidth: 800,
    padding: 40,
    alignItems: 'center',
  },
  title: {
    fontSize: 48,
    fontWeight: '600',
    color: '#2D3748',
    marginBottom: 40,
    textAlign: 'center',
    fontFamily: 'Cal Sans',
  },
  options: {
    width: '100%',
    gap: 16,
  },
  option: {
    width: '100%',
    padding: 20,
    borderRadius: 16,
    backgroundColor: 'white',
    borderWidth: 2,
    borderColor: '#E2E8F0',
    alignItems: 'center',
    cursor: 'pointer',
  },
  selectedOption: {
    borderColor: '#3182CE',
  },
  optionText: {
    fontSize: 20,
    fontWeight: '600',
    color: '#2D3748',
    fontFamily: 'Cal Sans',
  },
  selectedText: {
    color: 'white',
  },
  navigation: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '100%',
    marginTop: 40,
  },
  nextButton: {
    padding: 16,
    borderRadius: 12,
    backgroundColor: '#3182CE',
  },
  nextButtonDisabled: {
    backgroundColor: '#A0AEC0',
    cursor: 'not-allowed',
  },
  nextButtonText: {
    fontSize: 16,
    fontWeight: '600',
    color: 'white',
    fontFamily: 'Cal Sans',
  },
});

export default BeastModeQuestion;
