import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const App = () => {
  return (
    <View style={styles.container}>
      <View style={styles.hero}>
        <Text style={[styles.heroTitle, styles.calSans]}>Get greeted by the Yeti!</Text>
        <Text style={[styles.subtitle, styles.calSans]}>Create unforgettable moments with custom video messages from our charming AI Yeti</Text>

        <View style={styles.formContainer}>
          <div dangerouslySetInnerHTML={{
            __html: '<heyflow-wrapper flow-id="QZS4xUEaV1wbK9tOWtH6" dynamic-height scroll-up-on-navigation style-config=\'{"width":"800px"}\'></heyflow-wrapper>'
          }} />
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FAFAFA',
  },
  hero: {
    alignItems: 'center',
    paddingTop: 80,
    paddingBottom: 60,
  },
  heroTitle: {
    fontSize: 48,
    fontWeight: '600',
    color: '#2D3748',
    lineHeight: 60,
  },
  subtitle: {
    fontSize: 20,
    color: '#4A5568',
    marginTop: 20,
    marginBottom: 40,
    textAlign: 'center',
    maxWidth: 600,
    fontWeight: '600',
  },
  formContainer: {
    width: '100%',
    maxWidth: 800,
    backgroundColor: '#FAFAFA',
    borderRadius: 16,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.1,
    shadowRadius: 12,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 40,
    padding: 20,
  },
  calSans: {
    fontFamily: 'Cal Sans',
  },
});

export default App;
