import { StyleSheet } from 'react-native';

export const FORM_DIMENSIONS = {
  width: 800,
  minHeight: 500, // Slightly reduced to account for page padding
  padding: 40,
};

export const sharedStyles = StyleSheet.create({
  container: {
    width: '100%',
    maxWidth: FORM_DIMENSIONS.width,
    minHeight: FORM_DIMENSIONS.minHeight,
    padding: FORM_DIMENSIONS.padding,
    alignItems: 'center',
    justifyContent: 'space-between',
    backgroundColor: '#FAFAFA',
    marginBottom: 20, // Add margin to prevent overlap with footer
  },
  contentContainer: {
    width: '100%',
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    paddingBottom: 20, // Add padding to prevent content from touching navigation
  },
  title: {
    fontSize: 48,
    fontWeight: '600',
    color: '#2D3748',
    marginBottom: 40,
    textAlign: 'center',
    fontFamily: 'Cal Sans',
  },
  input: {
    width: '100%',
    padding: 16,
    borderRadius: 12,
    borderWidth: 2,
    borderColor: '#E2E8F0',
    fontSize: 18,
    color: '#2D3748',
    backgroundColor: 'white',
    fontFamily: 'Cal Sans',
  },
  navigation: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '100%',
    marginTop: 'auto', // Push navigation to bottom of container
    paddingTop: 20, // Add padding above navigation
  },
  backButton: {
    padding: 16,
    borderRadius: 12,
    backgroundColor: '#EDF2F7',
  },
  backButtonText: {
    fontSize: 16,
    fontWeight: '600',
    color: '#4A5568',
    fontFamily: 'Cal Sans',
  },
  nextButton: {
    padding: 16,
    borderRadius: 12,
    backgroundColor: '#3182CE',
  },
  nextButtonDisabled: {
    backgroundColor: '#A0AEC0',
    cursor: 'not-allowed',
  },
  nextButtonText: {
    fontSize: 16,
    fontWeight: '600',
    color: 'white',
    fontFamily: 'Cal Sans',
  },
});
