import React from 'react';
import { View, Text, StyleSheet, Pressable } from 'react-native';

const App = () => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={[styles.logo, styles.calSans]}>AI YETI</Text>
        <View style={styles.nav}>
          <Pressable style={styles.navItem}><Text style={[styles.navText, styles.calSans]}>How It Works</Text></Pressable>
          <Pressable style={styles.navItem}><Text style={[styles.navText, styles.calSans]}>Pricing</Text></Pressable>
          <Pressable style={[styles.navItem, styles.ctaButton]}><Text style={[styles.ctaText, styles.calSans]}>Get Started</Text></Pressable>
        </View>
      </View>

      <View style={styles.hero}>
        <Text style={[styles.heroTitle, styles.calSans]}>Get greeted by the Yeti!</Text>
        <Text style={[styles.subtitle, styles.calSans]}>Create unforgettable moments with custom video messages from our charming AI Yeti</Text>

        <View style={styles.formSection}>
          <View style={styles.formContainer}>
            <div dangerouslySetInnerHTML={{
              __html: '<heyflow-wrapper flow-id="QZS4xUEaV1wbK9tOWtH6" dynamic-height scroll-up-on-navigation style-config=\'{"width":"800px"}\'></heyflow-wrapper>'
            }} />
          </View>
          <img 
            src="/images/yeti_3.avif" 
            alt="Friendly AI Yeti"
            style={styles.yetiImage}
          />
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FAFAFA',
    minHeight: '100vh',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    padding: 20,
    paddingHorizontal: 40,
    backgroundColor: 'white',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
  },
  logo: {
    fontSize: 24,
    fontWeight: '600',
    color: '#2D3748',
  },
  nav: {
    flexDirection: 'row',
    alignItems: 'center',
    gap: 30,
  },
  navItem: {
    padding: 10,
  },
  navText: {
    color: '#4A5568',
    fontSize: 16,
    fontWeight: '600',
  },
  ctaButton: {
    backgroundColor: '#3182CE',
    borderRadius: 8,
    paddingHorizontal: 20,
    paddingVertical: 10,
  },
  ctaText: {
    color: 'white',
    fontWeight: '600',
  },
  hero: {
    flex: 1,
    alignItems: 'center',
    backgroundColor: '#FAFAFA',
    padding: 40,
  },
  heroTitle: {
    fontSize: 48,
    fontWeight: '600',
    color: '#2D3748',
    lineHeight: 60,
    textAlign: 'center',
  },
  subtitle: {
    fontSize: 20,
    color: '#4A5568',
    marginTop: 20,
    marginBottom: 40,
    textAlign: 'center',
    maxWidth: 600,
    fontWeight: '600',
  },
  formSection: {
    position: 'relative',
    width: '100%',
    maxWidth: 800,
    alignItems: 'center',
  },
  formContainer: {
    width: '100%',
    backgroundColor: '#FAFAFA',
    borderRadius: 16,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.1,
    shadowRadius: 12,
    alignItems: 'center',
    justifyContent: 'center',
    marginTop: 40,
    padding: 20,
    zIndex: 1,
  },
  yetiImage: {
    position: 'absolute',
    bottom: -100,
    right: -100,
    width: 400,
    height: 'auto',
    objectFit: 'contain',
    zIndex: 0,
  },
  calSans: {
    fontFamily: 'Cal Sans',
  },
});

export default App;
