import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import YetiForm from './components/YetiForm';

const App = () => {
  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={[styles.logo, styles.calSans]}>AI YETI</Text>
      </View>

      <View style={styles.mainContent}>
        <Text style={[styles.heroTitle, styles.calSans]}>Get greeted by the Yeti!</Text>

        <View style={styles.formSection}>
          <img 
            src="/images/yeti_3.png" 
            alt="Friendly AI Yeti"
            style={styles.yetiImage}
          />
          <View style={styles.formWrapper}>
            <YetiForm />
          </View>
        </View>
      </View>

      <View style={styles.footer}>
        <Text style={[styles.footerText, styles.calSans]}>© 2024 Pixel Party. All rights reserved.</Text>
        <Text style={[styles.footerText, styles.calSans]}>Made with ❤️ by the Yeti!</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    height: '100vh',
    backgroundColor: '#FAFAFA',
    display: 'flex',
    flexDirection: 'column',
    overflow: 'hidden',
  },
  header: {
    flexDirection: 'row',
    justifyContent: 'center',
    alignItems: 'center',
    padding: 8,
    paddingHorizontal: 40,
    backgroundColor: 'white',
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.1,
    shadowRadius: 4,
    height: '50px',
  },
  mainContent: {
    flex: 1,
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    paddingTop: 16,
    overflow: 'hidden',
  },
  logo: {
    fontSize: 20,
    fontWeight: '600',
    color: '#2D3748',
  },
  heroTitle: {
    fontSize: 32,
    fontWeight: '600',
    color: '#2D3748',
    lineHeight: 40,
    textAlign: 'center',
    marginBottom: 16,
  },
  formSection: {
    position: 'relative',
    width: '100%',
    maxWidth: 1000,
    alignItems: 'center',
    flex: 1,
    overflow: 'hidden',
  },
  formWrapper: {
    width: '100%',
    height: '100%',
    position: 'relative',
    zIndex: 2,
  },
  yetiImage: {
    position: 'absolute',
    bottom: -80,
    right: -80,
    width: 300,
    height: 'auto',
    objectFit: 'contain',
    zIndex: 1,
  },
  footer: {
    width: '100%',
    padding: 8,
    backgroundColor: '#FAFAFA',
    alignItems: 'center',
    justifyContent: 'center',
    gap: 4,
    borderTop: '1px solid #E2E8F0',
    height: '50px',
  },
  footerText: {
    fontSize: 12,
    color: '#4A5568',
    textAlign: 'center',
  },
  calSans: {
    fontFamily: 'Cal Sans',
  },
});

export default App;
