import React from 'react';
import { View, Text, StyleSheet, Image } from 'react-native';

const App = () => {
  return (
    <View style={styles.container}>
      <View style={styles.hero}>
        <View style={styles.heroContent}>
          <View style={styles.leftSection}>
            <Text style={[styles.heroTitle, styles.calSans]}>Get greeted by the Yeti!</Text>
            <Text style={[styles.subtitle, styles.calSans]}>Create unforgettable moments with custom video messages from our charming AI Yeti</Text>
            <Image 
              source={{ uri: '/images/yeti_1' }} 
              style={styles.yetiImage}
              resizeMode="contain"
            />
          </View>

          <View style={styles.formSection}>
            <div dangerouslySetInnerHTML={{
              __html: '<heyflow-wrapper flow-id="QZS4xUEaV1wbK9tOWtH6" dynamic-height scroll-up-on-navigation style-config=\'{"width":"100%"}\'></heyflow-wrapper>'
            }} />
          </View>
        </View>
      </View>

      <View style={styles.features}>
        <View style={styles.featureCard}>
          <Text style={[styles.featureTitle, styles.calSans]}>Personalized</Text>
          <Text style={[styles.featureText, styles.calSans]}>Every message is uniquely crafted for your recipient</Text>
        </View>
        <View style={styles.featureCard}>
          <Text style={[styles.featureTitle, styles.calSans]}>Instant</Text>
          <Text style={[styles.featureText, styles.calSans]}>Get your video within minutes</Text>
        </View>
        <View style={styles.featureCard}>
          <Text style={[styles.featureTitle, styles.calSans]}>Memorable</Text>
          <Text style={[styles.featureText, styles.calSans]}>Create lasting memories with our lovable Yeti</Text>
        </View>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#FAFAFA',
  },
  hero: {
    padding: '40px 40px 60px 40px',
    minHeight: 'calc(100vh - 200px)',
    display: 'flex',
    alignItems: 'center',
  },
  heroContent: {
    flexDirection: 'row',
    maxWidth: 1400,
    width: '100%',
    margin: '0 auto',
    gap: 60,
    alignItems: 'center',
  },
  leftSection: {
    flex: 1,
    paddingRight: 40,
  },
  formSection: {
    flex: 1,
    maxWidth: 600,
  },
  heroTitle: {
    fontSize: 56,
    fontWeight: '600',
    color: '#2D3748',
    lineHeight: 64,
    marginBottom: 24,
  },
  subtitle: {
    fontSize: 20,
    color: '#4A5568',
    marginBottom: 40,
    lineHeight: 28,
    fontWeight: '600',
  },
  yetiImage: {
    width: '100%',
    height: 400,
    marginTop: 40,
  },
  features: {
    flexDirection: 'row',
    justifyContent: 'center',
    gap: 30,
    padding: 60,
    backgroundColor: 'white',
  },
  featureCard: {
    flex: 1,
    maxWidth: 300,
    padding: 30,
    backgroundColor: '#F7FAFC',
    borderRadius: 12,
    alignItems: 'center',
    textAlign: 'center',
  },
  featureTitle: {
    fontSize: 24,
    fontWeight: '600',
    color: '#2D3748',
    marginBottom: 10,
  },
  featureText: {
    fontSize: 16,
    color: '#4A5568',
    textAlign: 'center',
    fontWeight: '600',
  },
  calSans: {
    fontFamily: 'Cal Sans',
  },
});

export default App;
